import React, { Component } from 'react'
import inputValues from '../Inputs'
import './info.css'

class Info extends Component {
    constructor() {
        super();

        this.yearsOfExp = inputValues["yearsOfExperience"];
        
        this.greetingTexts = [
            "I am an IT Engineer from Mumbai University graduated in June, 2018. I have professional experience of "+ this.yearsOfExp +" years and also an Azure Fundamentalist.",
            "I love to solve the technical challanges and to keep on learning new things. I started my journey in the field of programming from 2012. I started creating mini project as a part of second year curriculum to learn different kinds of technologies and how to integrated them together to for complete system. Studying different concepts of legacy coding, fundamentals of programing helped me to create a concrete base of Software Developer.",
            "As a part of this journey I attended various in-house events / tech meetups and also participated as iMac co-ordinator in Athatva College of Engineering for 2 years. I also self taught different technolodies like .NET Core , Bot Framework , Angular , Git & Github , DialogFlow and much more ...with excellency in languages C# , Java , Javascript , HTML , CSS , SQL.",
            "Over the years,I came to understand that field of sofware developemnt is like an open sandbox box of things. To create solutions with top notch developemnt techniques, integreting them with best Devops methods and create unique experiences is joy of my life. "
        ]

        this.languagesArray = [
            {
                lang : "Angular",
                profficiency : "70"
            },
            {
                lang : "Azure",
                profficiency : "70"
            },
            {
                lang : "Javascript",
                profficiency : "75"
            },
            {
                lang : "Dotnet Core",
                profficiency : "70"
            },
            {
                lang : "C-Sharp",
                profficiency : "75"
            },
            {
                lang : "MSSQL",
                profficiency : "70"
            },
            {
                lang : "Java",
                profficiency : "75"
            },
            {
                lang : "HTML + CSS",
                profficiency : "80"
            },
            {
                lang : "ReactJS",
                profficiency : "65"
            }
        ]
        
    }

    createProgresssStyleObj(value)
    {
        return { "width" : value + "%" }
    }

    render() {

        let items = [];
        let greetingItems = [];
        for (let index = 0; index < this.languagesArray.length; index++) {
            let obj = this.languagesArray[index];
            let language = obj["lang"];
            let proff = obj["profficiency"];
            if (language === "Azure") 
            {
                language = <b>{language} <span className="badge badge-success">Microsoft Certified</span></b>
            }
            else if (language === "ReactJS") 
            {
                language = <b>{language} <span className="badge badge-secondary">New</span></b>
            }
            else
            {
                language = <b>{language}</b>
            }
            items.push(
                    <div key={index} className="col-sm-12 skill-bar-section">
                        <div className="row">
                            {language}
                        </div>
                        <div className="row align-items-center">
                            <div className="col-sm-10 skill-progress-bar"> 
                                <div className="progress skill-bar">
                                    <div className="progress-bar bg-danger" role="progressbar" style={ this.createProgresssStyleObj(proff) }></div>
                                </div>
                            </div>
                            <div className="col-sm-2 text-left">
                                <b>{proff}%</b>
                            </div>
                        </div>
                    </div>
            )   
        }

        for (let index = 0; index < this.greetingTexts.length; index++) 
        {
            var obj = this.greetingTexts[index]
            greetingItems.push(
                <blockquote>{obj}</blockquote>
            );    
        }

        return (
            <div className="row">
                <div className="col-sm-6 banner-self-info text-left">
                    <h4 className="text-primary info-sec-header">Greetings !!</h4>
                    {greetingItems}
                </div>


                <div className="col-sm-6 banner-self-info text-left">
                    <h4 className="text-primary info-sec-header">Some skills to show</h4>
                    {items}
                </div>
            </div>
        )
    }
}

export default Info
